mapper.update:
update
	tbl_skill_storage
set
	  name = #{ name }
	, background_color = #{ background_color }
	, font_color = #{ font_color }
where
	uid = #{ uid }

update
	  tbl_skill as a
	, tbl_skill_storage as b
set
	  a.ability = #{ value }
	, b.ability = #{ value }
where
	a.uid = #{ uid } and b.uid = #{ uid };
